import React, { useState } from 'react';
import SummarizeText from './components/SummarizeText';
import WriteText from './components/WriteText';
import ChatBot from './components/ChatBot';
import './App.css';
// You might consider simple text labels instead of complex icons for Neumorphism
// import { FiBookOpen, FiEdit3, FiMessageSquare } from 'react-icons/fi'; // Feather icons for a light touch

function App() {
  const [activeTab, setActiveTab] = useState('chat'); // Default to chat

  const renderContent = () => {
    switch (activeTab) {
      case 'summarize':
        return <SummarizeText />;
      case 'write':
        return <WriteText />;
      case 'chat':
        return <ChatBot />;
      default:
        return <ChatBot />;
    }
  };

  return (
    <div className="app-outer-wrapper"> {/* New wrapper for overall neumorphic effect */}
      <div className="app-container">
        <header className="app-header">
          <div className="header-content">
            {/* You could use a simple text logo or a very minimalist icon here */}
            <h1>AI.CORE <span className="version-tag">2.0</span></h1>
            <p className="app-tagline">Intelligent Assistance</p>
          </div>
          <nav className="tabs-nav">
            <button
              className={`tab-button ${activeTab === 'summarize' ? 'active' : ''}`}
              onClick={() => setActiveTab('summarize')}
            >
              {/* <FiBookOpen className="tab-icon" /> */}
              Summarize
            </button>
            <button
              className={`tab-button ${activeTab === 'write' ? 'active' : ''}`}
              onClick={() => setActiveTab('write')}
            >
              {/* <FiEdit3 className="tab-icon" /> */}
              Write
            </button>
            <button
              className={`tab-button ${activeTab === 'chat' ? 'active' : ''}`}
              onClick={() => setActiveTab('chat')}
            >
              {/* <FiMessageSquare className="tab-icon" /> */}
              Chat
            </button>
          </nav>
        </header>
        <main className="app-content">
          {renderContent()}
        </main>
        <footer className="app-footer">
          <p>&copy; {new Date().getFullYear()} AI.CORE. All rights reserved.</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
