import React, { useState } from 'react';
import SummarizeText from './components/SummarizeText';
import WriteText from './components/WriteText';
import ChatBot from './components/ChatBot';
import './App.css';
// Icons có thể được tạo bằng CSS hoặc sử dụng thư viện như react-icons/fi
// import { FiTerminal, FiFeather, FiMessageCircle } from 'react-icons/fi';

function App() {
  const [activeTab, setActiveTab] = useState('chat'); // Mặc định là chat

  const renderContent = () => {
    switch (activeTab) {
      case 'summarize':
        return <SummarizeText />;
      case 'write':
        return <WriteText />;
      case 'chat':
        return <ChatBot />;
      default:
        return <ChatBot />;
    }
  };

  return (
    <div className="cyber-app-wrapper"> {/* Wrapper mới cho hiệu ứng tổng thể */}
      <div className="cyber-app-container">
        <header className="cyber-app-header">
          <div className="header-content">
            <span className="logo-glitch">AI_CORE</span>
            <h1 className="main-title">SYNTHESIS ENGINE <span className="version-tag">v3.0</span></h1>
            <p className="app-tagline">Neural Interface Activated</p>
          </div>
          <nav className="cyber-tabs-nav">
            <button
              className={`cyber-tab-button ${activeTab === 'summarize' ? 'active' : ''}`}
              onClick={() => setActiveTab('summarize')}
            >
              {/* <FiTerminal className="tab-icon" /> */}
              SUMMARIZE
            </button>
            <button
              className={`cyber-tab-button ${activeTab === 'write' ? 'active' : ''}`}
              onClick={() => setActiveTab('write')}
            >
              {/* <FiFeather className="tab-icon" /> */}
              GENERATE
            </button>
            <button
              className={`cyber-tab-button ${activeTab === 'chat' ? 'active' : ''}`}
              onClick={() => setActiveTab('chat')}
            >
              {/* <FiMessageCircle className="tab-icon" /> */}
              COMMUNICATE
            </button>
          </nav>
        </header>
        <main className="cyber-app-content">
          {renderContent()}
        </main>
      </div>
    </div>
  );
}

export default App;
