import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import rehypeRaw from "rehype-raw";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { oneDark } from "react-syntax-highlighter/dist/esm/styles/prism";

import "./MainContent.css";

const COHERE_API_KEY = process.env.REACT_APP_COHERE_API_KEY; // <-- ƒê·∫£m b·∫£o d√≤ng n√†y ƒë√∫ng
const COHERE_CHAT_API_URL = "https://api.cohere.com/v1/chat";

const MainContent = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setMessages([
      {
        sender: "ai",
        type: "text",
        text: "Ch√†o m·ª´ng b·∫°n ƒë·∫øn v·ªõi Gemini! B·∫°n c√≥ th·ªÉ h·ªèi t√¥i b·∫•t c·ª© ƒëi·ªÅu g√¨ v·ªÅ l·∫≠p tr√¨nh, thi·∫øt k·∫ø, ho·∫∑c c√°c ch·ªß ƒë·ªÅ kh√°c.",
      },
      {
        sender: "ai",
        type: "text", // Type v·∫´n l√† text, ReactMarkdown s·∫Ω x·ª≠ l√Ω markdown trong text
        text: `D∆∞·ªõi ƒë√¢y l√† m·ªôt v√≠ d·ª• v·ªÅ **Code Block** trong JavaScript:

\`\`\`javascript
function greet(name) {
  console.log(\`Hello, \${name}!\`);
}

greet("World");
\`\`\`

V√† ƒë√¢y l√† m·ªôt danh s√°ch:
* M·ª•c 1
* M·ª•c 2
`,
      },
    ]);
  }, []);

  // ... (handleSendMessage, handleKeyPress gi·ªØ nguy√™n nh∆∞ t√¥i ƒë√£ cung c·∫•p b·∫£n fix)

  // -------------------------------------------------------------
  // ƒêO·∫†N N√ÄY PH·∫¢I ƒê∆Ø·ª¢C X√ìA HO·∫∂C COMMENT HO√ÄN TO√ÄN
  // // Component con ƒë·ªÉ render Code Block (GI·ªÆ NGUY√äN ƒêO·∫†N N√ÄY L√Ä SAI)
  // const CodeBlockDisplay = ({ fileName, description, code }) => (
  //   <div className="code-content-wrapper">
  //     <div className="code-block-header">
  //       <span className="code-filename">{fileName}</span>
  //       <span className="code-description">{description}</span>
  //       <button className="copy-code-button">üìã</button>
  //       <button className="code-menu-button">‚ãÆ</button>
  //     </div>
  //     <pre className="code-content">
  //       <code className="language-text">{code}</code>
  //     </pre>
  //   </div>
  // );
  // -------------------------------------------------------------

  return (
    <main className="main-content">
      {/* ... (Header) ... */}

      <div className="content-body">
        <div className="chat-message-list">
          {/* ... (no-message-prompt) ... */}
          {messages.map((msg, index) => (
            <div key={index} className={`message-wrapper ${msg.sender}`}>
              {msg.sender === "ai" && <div className="message-avatar ai-avatar">AI</div>}

              <div className={`message-bubble ${msg.sender}`}>
                {/* --------------------------------------------------- */}
                {/* ƒê√ÇY L√Ä PH·∫¶N QUAN TR·ªåNG NH·∫§T */}
                {/* ƒê·∫£m b·∫£o KH√îNG c√≥ logic if/else d·ª±a tr√™n msg.type ·ªü ƒë√¢y */}
                {/* M√† CH·ªà D√ôNG ReactMarkdown cho t·∫•t c·∫£ c√°c tin nh·∫Øn AI */}
                {/* --------------------------------------------------- */}
                <ReactMarkdown
                  remarkPlugins={[remarkGfm]}
                  rehypePlugins={[rehypeRaw]}
                  components={{
                    a: ({ node, ...props }) => (
                      <a target="_blank" rel="noopener noreferrer" {...props} />
                    ),
                    code({ node, inline, className, children, ...props }) {
                      const match = /language-(\w+)/.exec(className || "");
                      return !inline && match ? (
                        <SyntaxHighlighter
                          children={String(children).replace(/\n$/, "")}
                          style={oneDark}
                          language={match[1]}
                          PreTag="div"
                          {...props}
                        />
                      ) : (
                        <code className={className} {...props}>
                          {children}
                        </code>
                      );
                    },
                  }}
                >
                  {msg.text} {/* V·∫´n l√† msg.text */}
                </ReactMarkdown>
              </div>

              {msg.sender === "user" && (
                <div className="message-avatar user-avatar">B·∫°n</div>
              )}
            </div>
          ))}
          {/* ... (isLoading v√† messagesEndRef) ... */}
        </div>
      </div>

      {/* ... (chat-input-area) ... */}
    </main>
  );
};

export default MainContent;
