import React, { useState } from 'react';
import SummarizeText from './components/SummarizeText';
import WriteText from './components/WriteText';
import ChatBot from './components/ChatBot';
import './App.css'; // Để thêm CSS

function App() {
  const [activeTab, setActiveTab] = useState('chat'); // Mặc định là tab Chat

  const renderContent = () => {
    switch (activeTab) {
      case 'summarize':
        return <SummarizeText />;
      case 'write':
        return <WriteText />;
      case 'chat':
        return <ChatBot />;
      default:
        return <ChatBot />;
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>AI Assistant</h1>
        <nav className="tabs-nav">
          <button
            className={`tab-button ${activeTab === 'summarize' ? 'active' : ''}`}
            onClick={() => setActiveTab('summarize')}
          >
            Tóm tắt đoạn văn
          </button>
          <button
            className={`tab-button ${activeTab === 'write' ? 'active' : ''}`}
            onClick={() => setActiveTab('write')}
          >
            Viết văn
          </button>
          <button
            className={`tab-button ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTab('chat')}
          >
            Chat
          </button>
        </nav>
      </header>
      <main className="app-content">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
