    try {
      if (!COHERE_API_KEY) {
        throw new Error(
          "Cohere API Key not found. Please set REACT_APP_COHERE_API_KEY in your .env file and restart your development server."
        );
      }

      const response = await fetch(COHERE_CHAT_API_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${COHERE_API_KEY}`,
          Accept: "application/json", // Ch·∫•p nh·∫≠n JSON response
        },
        body: JSON.stringify({
          message: newUserMessage.text,
          // G·ª≠i l·ªãch s·ª≠ chat ƒë·ªÉ AI c√≥ ng·ªØ c·∫£nh (quan tr·ªçng)
          chat_history: messages
            .filter((msg) => msg.type === "text") // Ch·ªâ g·ª≠i c√°c tin nh·∫Øn d·∫°ng text
            .map((msg) => ({
