          prompt_truncation: "AUTO",
          // connectors: [{ id: "web-search" }], // B·∫≠t t√¨m ki·∫øm web n·∫øu c·∫ßn
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Cohere API Error:", errorData);
        let errorMessage = `L·ªói API Cohere: ${response.status} ${response.statusText}`;
        if (errorData && errorData.message) {
          errorMessage += ` - ${errorData.message}`;
        } else if (errorData && errorData.detail) {
          errorMessage += ` - ${errorData.detail}`;
        }

        if (response.status === 401) {
          errorMessage += "\nVui l√≤ng ki·ªÉm tra l·∫°i API Key c·ªßa b·∫°n.";
        } else if (response.status === 429) {
          errorMessage +=
            "\nƒê√£ v∆∞·ª£t qu√° gi·ªõi h·∫°n t·ªëc ƒë·ªô y√™u c·∫ßu (rate limit). Vui l√≤ng th·ª≠ l·∫°i sau.";
        }
        throw new Error(errorMessage);
      }

      const data = await response.json();
      console.log("Cohere API Response:", data);

      const aiResponseText = data.text || "Kh√¥ng c√≥ ph·∫£n h·ªìi n√†o t·ª´ AI.";
      const aiResponse = { sender: "ai", type: "text", text: aiResponseText }; // AI tr·∫£ v·ªÅ text, ReactMarkdown s·∫Ω x·ª≠ l√Ω
      setMessages((prevMessages) => [...prevMessages, aiResponse]);
    } catch (error) {
      console.error("L·ªói khi g·ªçi API Cohere:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "ai", type: "text", text: `ƒê√£ c√≥ l·ªói x·∫£y ra: ${error.message}` },
      ]);
    } finally {
