import React, { useState, useRef, useEffect } from 'react';
import './MainContent.css';

// L·∫•y API Key t·ª´ bi·∫øn m√¥i tr∆∞·ªùng
const COHERE_API_KEY = "Zt7pG11iV7xlpPs4CgOiU20LOFGOGw4Scr4AH18p";
const COHERE_CHAT_API_URL = 'https://api.cohere.com/v1/chat';

const MainContent = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Kh·ªüi t·∫°o tin nh·∫Øn ch√†o m·ª´ng khi component mount
  useEffect(() => {
    setMessages([
      { sender: 'ai', type: 'text', text: 'Ch√†o m·ª´ng b·∫°n ƒë·∫øn v·ªõi Gemini! B·∫°n c√≥ th·ªÉ h·ªèi t√¥i b·∫•t c·ª© ƒëi·ªÅu g√¨ v·ªÅ l·∫≠p tr√¨nh, thi·∫øt k·∫ø, ho·∫∑c c√°c ch·ªß ƒë·ªÅ kh√°c.' },
    ]);
  }, []);

  // Cu·ªôn xu·ªëng cu·ªëi danh s√°ch tin nh·∫Øn m·ªói khi c√≥ tin m·ªõi
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // H√†m x·ª≠ l√Ω g·ª≠i tin nh·∫Øn
  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const newUserMessage = { sender: 'user', type: 'text', text: inputMessage };
    setMessages((prevMessages) => [...prevMessages, newUserMessage]);
    setInputMessage(''); // X√≥a n·ªôi dung input sau khi g·ª≠i
    setIsLoading(true); // B·∫Øt ƒë·∫ßu tr·∫°ng th√°i loading

    try {
      if (!COHERE_API_KEY) {
        throw new Error("Cohere API Key not found. Please set REACT_APP_COHERE_API_KEY in your .env file.");
      }

      const response = await fetch(COHERE_CHAT_API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${COHERE_API_KEY}`,
          'Accept': 'application/json', // Ch·∫•p nh·∫≠n JSON response
        },
        body: JSON.stringify({
          message: newUserMessage.text,
          // B·∫°n c√≥ th·ªÉ th√™m c√°c t√πy ch·ªçn kh√°c c·ªßa Cohere Chat API t·∫°i ƒë√¢y
          // V√≠ d·ª•:
          // chat_history: messages.filter(msg => msg.type === 'text').map(msg => ({
          //   role: msg.sender === 'user' ? 'USER' : 'CHATBOT',
          //   message: msg.text
          // })),
          // prompt_truncation: "AUTO",
          // connectors: [{ id: "web-search" }], // B·∫≠t t√¨m ki·∫øm web n·∫øu c·∫ßn
        }),
      });

      if (!response.ok) {
        // X·ª≠ l√Ω l·ªói HTTP (v√≠ d·ª•: 401 Unauthorized, 400 Bad Request, 500 Internal Server Error)
        const errorData = await response.json();
        console.error('Cohere API Error:', errorData);
        // Ki·ªÉm tra l·ªói c·ª• th·ªÉ ƒë·ªÉ ƒë∆∞a ra th√¥ng b√°o ph√π h·ª£p
        let errorMessage = `L·ªói API Cohere: ${response.status} ${response.statusText}`;
        if (errorData && errorData.message) {
            errorMessage += ` - ${errorData.message}`;
        } else if (errorData && errorData.detail) { // M·ªôt s·ªë l·ªói c√≥ th·ªÉ d√πng 'detail'
             errorMessage += ` - ${errorData.detail}`;
        }

        if (response.status === 401) {
            errorMessage += "\nVui l√≤ng ki·ªÉm tra l·∫°i API Key c·ªßa b·∫°n.";
        } else if (response.status === 429) {
            errorMessage += "\nƒê√£ v∆∞·ª£t qu√° gi·ªõi h·∫°n t·ªëc ƒë·ªô y√™u c·∫ßu (rate limit). Vui l√≤ng th·ª≠ l·∫°i sau.";
        }
        throw new Error(errorMessage);
      }

      const data = await response.json();
      console.log('Cohere API Response:', data);

      // L·∫•y c√¢u tr·∫£ l·ªùi t·ª´ ph·∫£n h·ªìi c·ªßa Cohere
      const aiResponseText = data.text || "Kh√¥ng c√≥ ph·∫£n h·ªìi n√†o t·ª´ AI.";
      const aiResponse = { sender: 'ai', type: 'text', text: aiResponseText };
      setMessages((prevMessages) => [...prevMessages, aiResponse]);

    } catch (error) {
      console.error("L·ªói khi g·ªçi API Cohere:", error);
      setMessages((prevMessages) => [...prevMessages, { sender: 'ai', type: 'text', text: `ƒê√£ c√≥ l·ªói x·∫£y ra: ${error.message}` }]);
    } finally {
      setIsLoading(false); // K·∫øt th√∫c tr·∫°ng th√°i loading
    }
  };

  // X·ª≠ l√Ω s·ª± ki·ªán nh·∫•n Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey && !isLoading && inputMessage.trim()) { // Th√™m !e.shiftKey ƒë·ªÉ cho ph√©p xu·ªëng d√≤ng b·∫±ng Shift+Enter
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Component con ƒë·ªÉ render Code Block (gi·ªØ nguy√™n)
  const CodeBlockDisplay = ({ fileName, description, code }) => (
    <div className="code-content-wrapper">
        <div className="code-block-header">
            <span className="code-filename">{fileName}</span>
            <span className="code-description">{description}</span>
            <button className="copy-code-button">üìã</button>
            <button className="code-menu-button">‚ãÆ</button>
        </div>
        <pre className="code-content">
            <code className="language-text">{code}</code> {/* ƒê·ªïi language-css sang language-text cho t·ªïng qu√°t */}
        </pre>
    </div>
  );

  return (
    <main className="main-content">
      <header className="main-header">
        <div className="header-left">
          <div className="gemini-title-small">Gemini</div>
          <div className="version-selector">
            2.5 Flash <span className="arrow-down">‚ñº</span>
          </div>
