import React, { useState } from 'react';
import SummarizeText from './components/SummarizeText';
import WriteText from './components/WriteText';
import ChatBot from './components/ChatBot';
import './App.css';
// Icons can be simple text or small SVG/font icons if needed
// import { FiEdit, FiBook, FiMessageSquare } from 'react-icons/fi';

function App() {
  const [activeTab, setActiveTab] = useState('chat');

  const renderContent = () => {
    switch (activeTab) {
      case 'summarize':
        return <SummarizeText />;
      case 'write':
        return <WriteText />;
      case 'chat':
        return <ChatBot />;
      default:
        return <ChatBot />;
    }
  };

  return (
    <div className="app-main-container">
      <header className="app-header">
        <div className="header-branding">
          <span className="app-logo">AI.Studio</span>
          <h1 className="app-title">Intelligent Workspace</h1>
        </div>
        <nav className="app-tabs-nav">
          <button
            className={`tab-item ${activeTab === 'summarize' ? 'active' : ''}`}
            onClick={() => setActiveTab('summarize')}
          >
            {/* <FiBook className="tab-icon" /> */}
            Summarize
          </button>
          <button
            className={`tab-item ${activeTab === 'write' ? 'active' : ''}`}
            onClick={() => setActiveTab('write')}
          >
            {/* <FiEdit className="tab-icon" /> */}
            Write
          </button>
          <button
            className={`tab-item ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTab('chat')}
          >
            {/* <FiMessageSquare className="tab-icon" /> */}
            Chat
          </button>
        </nav>
      </header>
      <main className="app-content-area">
        {renderContent()}
      </main>
      <footer className="app-footer">
        <p>&copy; {new Date().getFullYear()} AI.Studio. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
