import React, { useState } from 'react';
import SummarizeText from './components/SummarizeText';
import WriteText from './components/WriteText';
import ChatBot from './components/ChatBot';
import './App.css';
// React Icons (nếu muốn dùng):
// import { FaMicrochip, FaBookOpen, FaFeatherAlt, FaComments } from 'react-icons/fa';

function App() {
  const [activeTab, setActiveTab] = useState('chat'); // Mặc định là tab Chat

  const renderContent = () => {
    switch (activeTab) {
      case 'summarize':
        return <SummarizeText />;
      case 'write':
        return <WriteText />;
      case 'chat':
        return <ChatBot />;
      default:
        return <ChatBot />;
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="header-content">
          {/* <FaMicrochip className="app-icon" /> */} {/* Icon đại diện cho AI */}
          <h1>AI Core <span className="pro-tag">v2.0</span></h1> {/* Tên mới */}
        </div>
        <nav className="tabs-nav">
          <button
            className={`tab-button ${activeTab === 'summarize' ? 'active' : ''}`}
            onClick={() => setActiveTab('summarize')}
          >
            {/* <FaBookOpen className="tab-icon" /> */}
            <span className="tab-text">Tóm tắt</span>
          </button>
          <button
            className={`tab-button ${activeTab === 'write' ? 'active' : ''}`}
            onClick={() => setActiveTab('write')}
          >
            {/* <FaFeatherAlt className="tab-icon" /> */}
            <span className="tab-text">Viết</span>
          </button>
          <button
            className={`tab-button ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTab('chat')}
          >
            {/* <FaComments className="tab-icon" /> */}
            <span className="tab-text">Chat</span>
          </button>
        </nav>
      </header>
      <main className="app-content">
        {renderContent()}
      </main>
      <footer className="app-footer">
        <p>&copy; {new Date().getFullYear()} AI Core. Initiating the future.</p>
      </footer>
    </div>
  );
}

export default App;
