import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import rehypeRaw from "rehype-raw";
// Nếu dùng SyntaxHighlighter
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { oneDark } from "react-syntax-highlighter/dist/esm/styles/prism"; // Hoặc style khác
import "./MainContent.css";

// Lấy API Key từ biến môi trường
const COHERE_API_KEY = "Zt7pG11iV7xlpPs4CgOiU20LOFGOGw4Scr4AH18p";
const COHERE_CHAT_API_URL = "https://api.cohere.com/v1/chat";

const MainContent = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Khởi tạo tin nhắn chào mừng khi component mount
  useEffect(() => {
    setMessages([
      {
        sender: "ai",
        type: "text",
        text: "Chào mừng bạn đến với Gemini! Bạn có thể hỏi tôi bất cứ điều gì về lập trình, thiết kế, hoặc các chủ đề khác.",
      },
    ]);
  }, []);

  // Cuộn xuống cuối danh sách tin nhắn mỗi khi có tin mới
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Hàm xử lý gửi tin nhắn
  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const newUserMessage = { sender: "user", type: "text", text: inputMessage };
    setMessages((prevMessages) => [...prevMessages, newUserMessage]);
    setInputMessage(""); // Xóa nội dung input sau khi gửi
    setIsLoading(true); // Bắt đầu trạng thái loading

    try {
      if (!COHERE_API_KEY) {
        throw new Error("Cohere API Key not found. Please set REACT_APP_COHERE_API_KEY in your .env file.");
      }

      const response = await fetch(COHERE_CHAT_API_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${COHERE_API_KEY}`,
          Accept: "application/json", // Chấp nhận JSON response
        },
        body: JSON.stringify({
          message: newUserMessage.text,
          // Bạn có thể thêm các tùy chọn khác của Cohere Chat API tại đây
          // Ví dụ:
          // chat_history: messages.filter(msg => msg.type === 'text').map(msg => ({
          //   role: msg.sender === 'user' ? 'USER' : 'CHATBOT',
          //   message: msg.text
          // })),
          // prompt_truncation: "AUTO",
          // connectors: [{ id: "web-search" }], // Bật tìm kiếm web nếu cần
        }),
      });

      if (!response.ok) {
        // Xử lý lỗi HTTP (ví dụ: 401 Unauthorized, 400 Bad Request, 500 Internal Server Error)
        const errorData = await response.json();
        console.error("Cohere API Error:", errorData);
        // Kiểm tra lỗi cụ thể để đưa ra thông báo phù hợp
        let errorMessage = `Lỗi API Cohere: ${response.status} ${response.statusText}`;
        if (errorData && errorData.message) {
          errorMessage += ` - ${errorData.message}`;
        } else if (errorData && errorData.detail) {
          // Một số lỗi có thể dùng 'detail'
          errorMessage += ` - ${errorData.detail}`;
        }

        if (response.status === 401) {
          errorMessage += "\nVui lòng kiểm tra lại API Key của bạn.";
        } else if (response.status === 429) {
          errorMessage += "\nĐã vượt quá giới hạn tốc độ yêu cầu (rate limit). Vui lòng thử lại sau.";
        }
        throw new Error(errorMessage);
      }

      const data = await response.json();
      console.log("Cohere API Response:", data);

      const aiResponseText = data.text || "Không có phản hồi nào từ AI.";
      const aiResponse = { sender: "ai", type: "text", text: aiResponseText }; // Lưu trữ văn bản Markdown vào 'text'
      setMessages((prevMessages) => [...prevMessages, aiResponse]);
    } catch (error) {
      console.error("Lỗi khi gọi API Cohere:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "ai", type: "text", text: `Đã có lỗi xảy ra: ${error.message}` },
      ]);
    } finally {
      setIsLoading(false); // Kết thúc trạng thái loading
    }
  };

  // Xử lý sự kiện nhấn Enter
  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey && !isLoading && inputMessage.trim()) {
      // Thêm !e.shiftKey để cho phép xuống dòng bằng Shift+Enter
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Component con để render Code Block (giữ nguyên)
  const CodeBlockDisplay = ({ fileName, description, code }) => (
    <div className="code-content-wrapper">
      <div className="code-block-header">
        <span className="code-filename">{fileName}</span>
        <span className="code-description">{description}</span>
        <button className="copy-code-button">📋</button>
        <button className="code-menu-button">⋮</button>
      </div>
      <pre className="code-content">
        <code className="language-text">{code}</code> {/* Đổi language-css sang language-text cho tổng quát */}
      </pre>
    </div>
  );

  return (
    <main className="main-content">
      <header className="main-header">
        <div className="header-left">
          <div className="gemini-title-small">Gemini</div>
          <div className="version-selector">
            2.5 Flash <span className="arrow-down">▼</span>
          </div>
        </div>
        <div className="header-right">
          <button className="upgrade-button">
            <span className="star-icon">✨</span> Nâng cấp
          </button>
          <div className="user-avatar">
            <img src="https://via.placeholder.com/32/FF4081/FFFFFF?text=P" alt="User Avatar" />
          </div>
        </div>
      </header>

      <div className="content-body">
        <div className="chat-message-list">
          {messages.length === 0 && !isLoading && (
            <div className="no-message-prompt">Chào mừng bạn đến với Gemini! Hãy bắt đầu cuộc trò chuyện.</div>
          )}
          {messages.map((msg, index) => (
            <div key={index} className={`message-wrapper ${msg.sender}`}>
              {msg.sender === "ai" && <div className="message-avatar ai-avatar">AI</div>}

              {msg.type === "text" && (
                <div className={`message-bubble ${msg.sender}`}>
                  <p>{msg.text}</p>
                </div>
              )}
              {msg.type === "code" && (
                <CodeBlockDisplay fileName={msg.fileName} description={msg.description} code={msg.code} />
              )}

              {msg.sender === "user" && <div className="message-avatar user-avatar">Bạn</div>}
            </div>
          ))}
          {isLoading && (
            <div className="message-wrapper ai">
              <div className="message-avatar ai-avatar">AI</div>
              <div className="message-bubble ai typing-indicator">
                <p>AI đang gõ...</p>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      <div className="chat-input-area">
        <div className="input-row">
          <button className="input-action-button">+ </button>
          <textarea
            className="chat-input"
            placeholder="Gửi tin nhắn cho Gemini"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            rows={1}
            onInput={(e) => {
              e.target.style.height = "auto";
              e.target.style.height = e.target.scrollHeight + "px";
            }}
            disabled={isLoading}
          />
          <button className="input-action-button icon-button" disabled={isLoading}>
            {" "}
            {/* Disable buttons when loading */}
            <span className="icon"></span>
            Deep Research
          </button>
          <button className="input-action-button icon-button" disabled={isLoading}>
            {" "}
            {/* Disable buttons when loading */}
            <span className="icon"></span>
            Canvas
          </button>
          <button className="send-button" onClick={handleSendMessage} disabled={isLoading || !inputMessage.trim()}>
            ➤
          </button>
        </div>
        <div className="chat-footer-text">
          Gemini có thể mắc sai sót, vì vậy, hãy xác minh các câu trả lời của Gemini
        </div>
      </div>
    </main>
  );
};

export default MainContent;
